sys_ui_action.script:
action.setRedirectURL(current);
const prevTurn = +current.getValue('current_turn');
const turnsGr = new GlideRecord('x_965810_zombie_turn');
let zoGr = new GlideRecord('x_965810_zombie_zombie');
let czGr = new GlideRecord('x_965810_zombie_citizen');
czGr.addQuery('game', current.getUniqueValue());
czGr.query();
zoGr.addQuery('game', current.getUniqueValue());
zoGr.query();

if (zoGr.hasNext() && czGr.hasNext()) {
    const citizens = [];
    while (czGr.next()) {
        citizens.push({
            name: czGr.getValue('name'),
            sys_id: czGr.getUniqueValue()
        });
    }
    turnsGr.addQuery('game', current.getUniqueValue());
    turnsGr.addQuery('number', prevTurn.toString());
    turnsGr.query();
    turnsGr.next();

    while (zoGr.next()) {
        const randPick = Math.floor(Math.random() * citizens.length);
        const randCz = citizens[randPick];
        const czDice = Math.round(Math.random() * 6);
        const zoDice = Math.round(Math.random() * 6);
        if (czDice < zoDice) {
            randCz.dead = true;
            if (Math.random() < 0.5) {
                const newZombie = new GlideRecord('x_965810_zombie_zombie');
                newZombie.initialize();
                newZombie.setValue('name', 'Minion Zombie');
                newZombie.setValue('game', current.getUniqueValue());
                newZombie.insert();
            }
        } else if (czDice > zoDice) {
            zoGr.deleteRecord();
        } else {
            randCz.dead = true;
            zoGr.deleteRecord();
        }
    }
    zoGr = new GlideRecord('x_965810_zombie_zombie');
    czGr = new GlideRecord('x_965810_zombie_citizen');
	czGr.addQuery('game', current.getUniqueValue());
	zoGr.addQuery('game', current.getUniqueValue());
    zoGr.query();
    czGr.query();
    let citizensCount = 0;
    while (czGr.next()) {
        const citizen = citizens.find(cz => cz.sys_id === czGr.getUniqueValue());
        if (citizen.dead) {
            //gs.info('line 45');
            czGr.deleteRecord();
        } else {
            citizensCount++;
        }
    }
    let zombiesCount = 0;
    if (zoGr.hasNext()) {
        zombiesCount = zoGr.getRowCount();
    }
    if (citizensCount > 0 && zombiesCount > 0) {
        const newTurn = new GlideRecord('x_965810_zombie_turn');
        newTurn.initialize();
        newTurn.setValue('game', current.getUniqueValue());
        newTurn.setValue('number', prevTurn + 1);
        newTurn.setValue('citizens', citizensCount);
        newTurn.setValue('zombies', zombiesCount);
        newTurn.insert();
    } else {
        if (citizensCount == 0) {
            current.setValue('state', 3);
            gs.addInfoMessage('Zombies eat everybody...');
        } else {
            current.setValue('state', 2);
            gs.addInfoMessage('Citizens WIN!');
        }
    }
    current.setValue('current_turn', prevTurn + 1);
    current.update();
}
